'use strict';

// MAIN //

/**
* Flattens an array of arrays and assigns elements in row-major order to a provided strided array.
*
* ## Notes
*
* -   The function assumes that all nested arrays have the same length (i.e., the input array is **not** a ragged array).
*
* @param {Array<Array>} arr - array of arrays
* @param {Array} out - output array
* @param {integer} stride - output array stride
* @param {integer} offset - output array index offset
* @returns {Array} output array
*
* @example
* var x = [ [ 1, 2 ], [ 3, 4 ] ];
*
* var out = flatten2d( x, [ 0, 0, 0, 0 ], 1, 0 );
* // returns [ 1, 2, 3, 4 ]
*/
function flatten2d( arr, out, stride, offset ) {
	var io;
	var M;
	var N;
	var a;
	var i;
	var j;

	M = arr.length;
	N = arr[ 0 ].length;
	io = offset;
	for ( i = 0; i < M; i++ ) {
		a = arr[ i ];
		for ( j = 0; j < N; j++ ) {
			out[ io ] = a[ j ];
			io += stride;
		}
	}
	return out;
}


// EXPORTS //

module.exports = flatten2d;
