'use strict';

/**
* Normalize values in preparation for broadcasting.
*
* @module @stdlib/gsheets/array/normalize-broadcast-args
*
* @example
* var normalize = require( '@stdlib/gsheets/array/normalize-broadcast-args' );
*
* var values = [ [ [ 1, 2 ] ], 3 ];
* var dtypes = [ 'number', 'number' ];
* var msgs = [ 'First argument', 'Second argument' ];
*
* var out = normalize( values, dtypes, msgs );
* // returns <Array>
*
* var v = out[ 0 ];
* // returns [ [ [ 1, 2 ] ], [ [ 3 ] ] ]
*
* var sh = out[ 1 ];
* // returns [ [ 1, 2 ], [ 1, 1 ] ]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
