'use strict';

// MODULES //

var format = require( '@stdlib/string-format' );
var base = require( '@stdlib/gsheets/assert/base/is-broadcast-compatible-with' );


// MAIN //

/**
* Asserts whether a second input range is broadcast compatible with a provided input range.
*
* @param {Array<Array>} x - first input range (i.e., the range to test against)
* @param {Array<Array>} y - second input range
* @param {string} subject - error message subject
* @param {string} object - error message object
* @throws {TypeError} must provide a broadcast compatible range
* @returns {void}
*
* @example
* var x = [ [ 1, 2 ] ];
* var y = [ [ 3, 4 ] ];
*
* try {
*     isBroadcastCompatibleWith( x, y, 'First argument', 'second argument' );
* } catch ( error ) {
*     console.error( error.message );
* }
*/
function isBroadcastCompatibleWith( x, y, subject, object ) {
	if ( base( x, y ) ) {
		return;
	}
	throw new TypeError( format( 'invalid argument. %s must be broadcast compatible with %s.', subject, object ) );
}


// EXPORTS //

module.exports = isBroadcastCompatibleWith;
