'use strict';

/**
* Assert whether two input ranges are broadcast compatible.
*
* @module @stdlib/gsheets/assert/is-broadcast-compatible
*
* @example
* var isBroadcastCompatible = require( '@stdlib/gsheets/assert/is-broadcast-compatible' );
*
* var x = [ [ 1.0, 2.0 ] ];
* var y = [ [ 3.0, 4.0 ] ];
*
* try {
*     isBroadcastCompatible( x, y, 'First and second arguments' );
* } catch ( error ) {
*     console.error( error.message );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
