'use strict';

/**
* Assert if a buffer length is compatible with ndarray meta data.
*
* @module @stdlib/gsheets/assert/is-buffer-length-compatible
*
* @example
* var isBufferLengthCompatible = require( '@stdlib/gsheets/assert/is-buffer-length-compatible' );
*
* var shape = [ 2, 2 ];
* var strides = [ 2, 1 ];
* var offset = 25;
*
* try {
*     isBufferLengthCompatible( 30, shape, strides, offset );
* } catch ( error ) {
*     console.error( error.message );
* }
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
