'use strict';

// MODULES //

var format = require( '@stdlib/string-format' );


// MAIN //

/**
* Asserts whether a value is greater than another value.
*
* @param {number} x - first value
* @param {number} y - second value
* @param {string} subject - error message subject
* @param {string} object - error message object
* @throws {TypeError} first value must be greater than the second value
* @returns {void}
*
* @example
* try {
*     isGreaterThan( 4.0, 3.0, 'First argument', 'the second argument' );
* } catch ( error ) {
*     console.error( error.message );
* }
*/
function assert( x, y, subject, object ) {
	if ( x > y ) {
		return;
	}
	throw new TypeError( format( 'invalid argument. %s must be greater than %s. Values: [%s, %s].', subject, object, String( x ), String( y ) ) );
}


// EXPORTS //

module.exports = assert;
