'use strict';

// MODULES //

var format = require( '@stdlib/string-format' );
var isInteger = require( '@stdlib/assert-is-integer' ).isPrimitive;


// MAIN //

/**
* Asserts whether ndarray strides are valid.
*
* @param {Array} strides - list of dimension sizes
* @param {string} msg - error message subject
* @throws {TypeError} must provide an array containing integers
* @returns {void}
*
* @example
* var strides = [ 1, 2, 3 ];
*
* try {
*     isValidStrides( strides, 'First argument' );
* } catch ( error ) {
*     console.error( error.message );
* }
*/
function isValidStrides( strides, msg ) {
	var i;
	for ( i = 0; i < strides.length; i++ ) {
		if ( !isInteger( strides[ i ] ) ) {
			throw new TypeError( format( 'invalid argument. %s must contain only integers.', msg ) );
		}
	}
}


// EXPORTS //

module.exports = isValidStrides;
