'use strict';

// MODULES //

var broadcastShapes = require( '@stdlib/ndarray-base-broadcast-shapes' );
var bbinary2d = require( '@stdlib/array-base-broadcasted-binary2d' );
var zeros2d = require( '@stdlib/array-base-zeros2d' );
var format = require( '@stdlib/string-format' );
var normalizeBroadcastArgs = require( '@stdlib/gsheets/array/normalize-broadcast-args' );
var wrap = require( '@stdlib/gsheets/math/tools/wrap-binary' );


// VARIABLES //

var DTYPES = [ 'number', 'number' ];
var MSGS = [ 'First argument', 'Second argument' ];


// MAIN //

/**
* Applies a binary function which accepts two numbers and returns a number.
*
* @param {Array} args - function arguments
* @param {Function} fcn - binary function to apply
* @throws {Error} unrecognized option
* @throws {Error} input arguments must be broadcast compatible
* @returns {Array<Array>} results
*
* @example
* function add( x, y ) {
*     return x + y;
* }
*
* var x = [ [ 1, 2 ] ];
* var y = [ [ 3, 4 ] ];
*
* var out = binary2d( [ x, y ], add );
* // returns [ [ 4, 6 ] ]
*/
function binary2d( args, fcn ) {
	var shape;
	var opts;
	var out;
	var f;
	var o;
	var i;

	opts = {
		'nonnumeric': void 0,
		'nan': NaN,
		'pinf': Infinity,
		'ninf': -Infinity
	};
	for ( i = 2; i < args.length; i += 2 ) {
		o = args[ i ];
		if ( o === 'nonnumeric' || o === 'nan' || o === 'pinf' || o === 'ninf' ) {
			opts[ o ] = args[ i+1 ];
		} else {
			throw new Error( format( 'invalid argument. Unrecognized option name. Value: %s.', String( o ) ) );
		}
	}
	// Normalize the provided arguments so we are always working with nested arrays:
	args = normalizeBroadcastArgs( [ args[ 0 ], args[ 1 ] ], DTYPES, MSGS );

	// Resolve the shape of the broadcasted result:
	shape = broadcastShapes( args[ 1 ] );
	if ( shape === null ) {
		throw new Error( 'invalid argument. Input arguments are not broadcast compatible.' );
	}
	// Create an output array:
	out = zeros2d( shape );

	// Wrap the lower-level function to ensure proper handling of input arguments and return values:
	f = wrap( fcn, opts );

	// Apply the function to broadcasted arrays:
	args[ 0 ].push( out );
	args[ 1 ].push( shape );
	bbinary2d( args[ 0 ], args[ 1 ], f );
	return out;
}


// EXPORTS //

module.exports = binary2d;
