'use strict';

/**
* Apply a ternary function which accepts three numbers and returns a number.
*
* @module @stdlib/gsheets/math/tools/ternary
*
* @example
* var ternary = require( '@stdlib/gsheets/math/tools/ternary' );
*
* function add( x, y, z ) {
*     return x + y + z;
* }
*
* var x = [ [ 1, 2 ] ];
* var y = [ [ 3, 4 ] ];
* var z = [ [ 5, 6 ] ];
*
* var out = ternary( [ x, y, z ], add );
* // returns [ [ 9, 12 ] ]
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
