'use strict';

// MODULES //

var isArray = require( '@stdlib/assert-is-array' );
var unary2d = require( '@stdlib/array-base-unary2d' );
var format = require( '@stdlib/string-format' );
var wrap = require( '@stdlib/gsheets/math/tools/wrap-unary' );


// MAIN //

/**
* Applies a unary function which accepts a number and returns a number.
*
* @param {Array} args - function arguments
* @param {Function} fcn - unary function to apply
* @throws {Error} unrecognized option
* @returns {Array<Array>} results
*
* @example
* function fcn( x ) {
*     return x;
* }
*
* var x = [ [ 1, 2 ] ];
*
* var out = unary( [ x ], fcn );
* // returns [ [ 1, 2 ] ]
*/
function unary( args, fcn ) {
	var value;
	var opts;
	var f;
	var o;
	var i;

	opts = {
		'nonnumeric': void 0,
		'nan': NaN,
		'pinf': Infinity,
		'ninf': -Infinity
	};
	value = args[ 0 ];
	for ( i = 1; i < args.length; i += 2 ) {
		o = args[ i ];
		if ( o === 'nonnumeric' || o === 'nan' || o === 'pinf' || o === 'ninf' ) {
			opts[ o ] = args[ i+1 ];
		} else {
			throw new Error( format( 'invalid argument. Unrecognized option name. Value: %s.', String( o ) ) );
		}
	}
	f = wrap( fcn, opts );
	if ( isArray( value ) ) {
		// Use the input array as the output array, thus performing an in-place operation, based on the assumption that such mutation is allowed and allowing us to avoid unnecessary memory allocation for better performance:
		unary2d( [ value, value ], [ value.length, value[ 0 ].length ], f );
		return value;
	}
	return f( value );
}


// EXPORTS //

module.exports = unary;
