'use strict';

/**
* Wrap a quaternary function which accepts four numbers and returns a number.
*
* @module @stdlib/gsheets/math/tools/wrap-quaternary
*
* @example
* var wrap = require( '@stdlib/gsheets/math/tools/wrap-quaternary' );
*
* function add( x, y, z, w ) {
*     return x + y + z + w;
* }
*
* var f = wrap( add, {
*     'nonnumeric': '',
*     'nan': '',
*     'pinf': '',
*     'ninf': ''
* });
* // returns <Function>
*
* var v = f( 1.0, 2.0, 3.0, 4.0 );
* // returns 10.0
*/

// MODULES //

var main = require( './main.js' );


// EXPORTS //

module.exports = main;
