'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils-define-read-only-property' );
var AFINN_111 = require( '@stdlib/datasets-afinn-111' );
var AFINN_96 = require( '@stdlib/datasets-afinn-96' );
var ANSCOMBES_QUARTET = require( '@stdlib/datasets-anscombes-quartet' );
var HERNDON_VENUS_SEMIDIAMETERS = require( '@stdlib/datasets-herndon-venus-semidiameters' ); // eslint-disable-line id-length
var NIGHTINGALES_ROSE = require( '@stdlib/datasets-nightingales-rose' );


// MAIN //

/**
* Adds APIs to a provided namespace.
*
* @private
* @param {Object} dest - destination namespace
* @returns {Object} destination namespace
*/
function namespace( dest ) {
	setReadOnly( dest, 'AFINN_111', AFINN_111 );
	setReadOnly( dest, 'AFINN_96', AFINN_96 );
	setReadOnly( dest, 'ANSCOMBES_QUARTET', ANSCOMBES_QUARTET );
	setReadOnly( dest, 'HERNDON_VENUS_SEMIDIAMETERS', HERNDON_VENUS_SEMIDIAMETERS );
	setReadOnly( dest, 'NIGHTINGALES_ROSE', NIGHTINGALES_ROSE );
	return dest;
}


// EXPORTS //

module.exports = namespace;
