'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils-define-read-only-property' );
var iterRows = require( '@stdlib/ndarray-iter-rows' );
var ndarray = require( '@stdlib/ndarray-base-ctor' );
var ndarray2range = require( '@stdlib/gsheets/ndarray/ndarray2range' );
var nextCartesianIndex = require( '@stdlib/ndarray-base-next-cartesian-index' );
var numel = require( '@stdlib/ndarray-base-numel' );
var range2ndarray = require( '@stdlib/gsheets/ndarray/range2ndarray' );
var seq2multislice = require( '@stdlib/slice-base-seq2multislice' );
var shape2strides = require( '@stdlib/ndarray-base-shape2strides' );
var slice = require( '@stdlib/ndarray-base-slice' );
var strides2offset = require( '@stdlib/ndarray-base-strides2offset' );
var toArray = require( '@stdlib/ndarray-base-to-array' );
var zeros = require( '@stdlib/ndarray-base-zeros' );


// MAIN //

/**
* Adds APIs to a provided namespace.
*
* @private
* @param {Object} dest - destination namespace
* @returns {Object} destination namespace
*/
function namespace( dest ) {
	setReadOnly( dest, 'iterRows', iterRows );
	setReadOnly( dest, 'ndarray', ndarray );
	setReadOnly( dest, 'ndarray2range', ndarray2range );
	setReadOnly( dest, 'nextCartesianIndex', nextCartesianIndex );
	setReadOnly( dest, 'numel', numel );
	setReadOnly( dest, 'range2ndarray', range2ndarray );
	setReadOnly( dest, 'seq2multislice', seq2multislice );
	setReadOnly( dest, 'shape2strides', shape2strides );
	setReadOnly( dest, 'slice', slice );
	setReadOnly( dest, 'strides2offset', strides2offset );
	setReadOnly( dest, 'toArray', toArray );
	setReadOnly( dest, 'zeros', zeros );
	return dest;
}


// EXPORTS //

module.exports = namespace;
