'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils-define-read-only-property' );
var bartlettTest = require( '@stdlib/stats-bartlett-test' );
var cumax = require( '@stdlib/stats-base-cumax' ).ndarray;
var max = require( '@stdlib/stats-base-max' ).ndarray;
var mskmax = require( '@stdlib/stats-base-mskmax' ).ndarray;

var dists = require( './dists' );


// MAIN //

/**
* Adds APIs to a provided namespace.
*
* @private
* @param {Object} dest - destination namespace
* @returns {Object} destination namespace
*/
function namespace( dest ) {
	setReadOnly( dest, 'bartlettTest', bartlettTest );
	setReadOnly( dest, 'cumax', cumax );
	setReadOnly( dest, 'max', max );
	setReadOnly( dest, 'mskmax', mskmax );

	setReadOnly( dest, 'dists', dists( {} ) );

	return dest;
}


// EXPORTS //

module.exports = namespace;
