/* eslint-disable camelcase */

'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils-define-read-only-property' );
var s_o = require( '@stdlib/gsheets/string/tools/wrap-unary' );
var ss_o = require( '@stdlib/gsheets/string/tools/wrap-binary' );
var sss_o = require( '@stdlib/gsheets/string/tools/wrap-ternary' );


// MAIN //

/**
* Adds APIs to a provided namespace.
*
* @private
* @param {Object} dest - destination namespace
* @returns {Object} destination namespace
*/
function namespace( dest ) {
	setReadOnly( dest, 's_o', s_o );
	setReadOnly( dest, 'ss_o', ss_o );
	setReadOnly( dest, 'sss_o', sss_o );
	return dest;
}


// EXPORTS //

module.exports = namespace;
